              $("input:radio").change(function() {
                  var meta_id = $(this).attr("data-meta-id");
                  var remote = $(this).val();
                  var local_xml = $.ajax({
                    url: 'ajaxGetObjAttrs',
                    data:{meta_id:meta_id},
