                    + '<table>'
                    + '<tr>'
                    + '<th>Remote</th>'
                    + '<th>Local</th>'
                    + '</tr>';
                  var skip = ['attr_cacheable','active','attr_active_end','attr_active_start','change_dt','change_history','change_uid','created_dt','created_uid','internal_dict','master_version','major_version','minor_version','work_dt','work_uid'];
